import React, { useState } from 'react';

const generations = [
  { value: '', label: 'Todas' },
  { value: '1', label: 'Generación 1' },
  { value: '2', label: 'Generación 2' },
  { value: '3', label: 'Generación 3' },
  { value: '4', label: 'Generación 4' },
  { value: '5', label: 'Generación 5' },
  { value: '6', label: 'Generación 6' },
  { value: '7', label: 'Generación 7' },
  { value: '8', label: 'Generación 8' },
  { value: '9', label: 'Generación 9' },
];

const types = [
  '', 'normal', 'fire', 'water', 'electric', 'grass', 'ice', 'fighting', 'poison',
  'ground', 'flying', 'psychic', 'bug', 'rock', 'ghost', 'dragon', 'dark', 'steel', 'fairy'
];

const habitats = [
  '', 'cave', 'forest', 'grassland', 'mountain', 'rare', 'rough-terrain', 'sea', 'urban', 'waters-edge'
];

const Search = () => {
  const [criteria, setCriteria] = useState({
    number: '',
    name: '',
    type: '',
    generation: '',
    habitat: ''
  });

  const handleChange = e => {
    setCriteria({ ...criteria, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    // Aquí iría la lógica de búsqueda
    alert('Búsqueda: ' + JSON.stringify(criteria, null, 2));
  };

  return (
    <div className="search">
      <h1>Buscar Pokémon</h1>
      <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: '0 auto', textAlign: 'left' }}>
        <label>
          Número de Pokédex:
          <input
            type="number"
            name="number"
            value={criteria.number}
            onChange={handleChange}
            min="1"
            placeholder="Ej: 25"
          />
        </label>
        <br />
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={criteria.name}
            onChange={handleChange}
            placeholder="Ej: Pikachu"
          />
        </label>
        <br />
        <label>
          Tipo:
          <select name="type" value={criteria.type} onChange={handleChange}>
            {types.map(type => (
              <option key={type} value={type}>{type ? type.charAt(0).toUpperCase() + type.slice(1) : 'Todos'}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Generación:
          <select name="generation" value={criteria.generation} onChange={handleChange}>
            {generations.map(gen => (
              <option key={gen.value} value={gen.value}>{gen.label}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Hábitat:
          <select name="habitat" value={criteria.habitat} onChange={handleChange}>
            <option value="">Todos</option>
            {habitats.filter(h => h).map(habitat => (
              <option key={habitat} value={habitat}>{habitat.charAt(0).toUpperCase() + habitat.slice(1)}</option>
            ))}
          </select>
        </label>
        <br />
        <button type="submit">Buscar</button>
      </form>
    </div>
  );
};

export default Search;
