import React, { useEffect, useState } from 'react';
import PokemonCard from '../components/PokemonCard';
import PokedexFilters from '../components/PokedexFilters';
import '../styles/Pokedex.css';

const LIMIT = 50;

const generations = [
  { value: '', label: 'Todas' },
  { value: '1', label: 'Generación 1' },
  { value: '2', label: 'Generación 2' },
  { value: '3', label: 'Generación 3' },
  { value: '4', label: 'Generación 4' },
  { value: '5', label: 'Generación 5' },
  { value: '6', label: 'Generación 6' },
  { value: '7', label: 'Generación 7' },
  { value: '8', label: 'Generación 8' },
  { value: '9', label: 'Generación 9' },
];

const types = [
  '', 'normal', 'fire', 'water', 'electric', 'grass', 'ice', 'fighting', 'poison',
  'ground', 'flying', 'psychic', 'bug', 'rock', 'ghost', 'dragon', 'dark', 'steel', 'fairy'
];

const initialFilters = {
  name: '',
  type: '',
  generation: '',
  number: ''
};

const generationRanges = {
  '1': [1, 151],
  '2': [152, 251],
  '3': [252, 386],
  '4': [387, 493],
  '5': [494, 649],
  '6': [650, 721],
  '7': [722, 809],
  '8': [810, 905],
  '9': [906, 1025],
};

const Pokedex = () => {
  const [pokemons, setPokemons] = useState([]);
  const [allPokemons, setAllPokemons] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState(initialFilters);
  const [filteredList, setFilteredList] = useState([]);
  const [offset, setOffset] = useState(LIMIT);

  // Cargar todos los pokémon una vez para filtrar localmente
  useEffect(() => {
    const fetchAllPokemons = async () => {
      setLoading(true);
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon?limit=1300&offset=0`);
        const data = await response.json();
        const detailedPokemons = await Promise.all(
          data.results.map(async (pokemon) => {
            const res = await fetch(pokemon.url);
            const poke = await res.json();
            // Obtener generación
            const speciesRes = await fetch(poke.species.url);
            const speciesData = await speciesRes.json();
            // Extraer el número de generación como string
            const genMatch = speciesData.generation.url.match(/generation-(\d+)/);
            const generation = genMatch ? genMatch[1] : '';
            return {
              ...poke,
              generation: String(generation),
            };
          })
        );
        setAllPokemons(detailedPokemons);
        setFilteredList(detailedPokemons); // <--- Importante
        setPokemons(detailedPokemons.slice(0, LIMIT)); // <--- Importante
        setOffset(LIMIT);
      } catch (error) {
        console.error('Error fetching Pokémon:', error);
      }
      setLoading(false);
    };
    fetchAllPokemons();
  }, []);

  // Filtrar solo cuando se aprieta el botón "Buscar"
  const handleFilter = (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      const noFilters =
        !filters.name && !filters.type && !filters.generation && !filters.number;

      const filtered = noFilters
        ? allPokemons
        : allPokemons.filter(pokemon => {
            const matchesName = filters.name
              ? pokemon.name.toLowerCase().includes(filters.name.toLowerCase())
              : true;
            const matchesType = filters.type
              ? pokemon.types.some(t => t.type.name === filters.type)
              : true;
            const matchesGen = filters.generation
              ? (
                  pokemon.id >= generationRanges[filters.generation][0] &&
                  pokemon.id <= generationRanges[filters.generation][1]
                )
              : true;
            const matchesNumber = filters.number
              ? String(pokemon.id) === String(filters.number)
              : true;
            return matchesName && matchesType && matchesGen && matchesNumber;
          });
      setFilteredList(filtered);
      setPokemons(filtered.slice(0, LIMIT));
      setOffset(LIMIT);
      setLoading(false);
    }, 100);
  };

  // Limpiar filtros y mostrar todos
  const handleClear = () => {
    setFilters(initialFilters);
    setFilteredList(allPokemons);
    setPokemons(allPokemons.slice(0, LIMIT));
    setOffset(LIMIT);
  };

  // Cargar más pokémon filtrados
  const handleLoadMore = () => {
    const more = filteredList.slice(offset, offset + LIMIT);
    setPokemons([...pokemons, ...more]);
    setOffset(offset + LIMIT);
  };

  const handleChange = e => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  if (loading && pokemons.length === 0) {
    return <div style={{ marginTop: 40, textAlign: 'center' }}>Cargando...</div>;
  }

  return (
    <div>
      <PokedexFilters
        filters={filters}
        types={types}
        generations={generations}
        handleChange={handleChange}
        handleFilter={handleFilter}
        handleClear={handleClear}
      />

      <div className="pokemon-list" tabIndex={-1}>
        {!loading && pokemons.length === 0 && <div>No hay pokémon para mostrar</div>}
        {pokemons.map((pokemon) => (
          <PokemonCard key={pokemon.id} pokemon={pokemon} />
        ))}
      </div>
      <div style={{ textAlign: 'center', margin: '20px' }}>
        {pokemons.length > 0 && pokemons.length < filteredList.length && (
          <button onClick={handleLoadMore} disabled={loading}>
            {loading ? 'Cargando...' : 'Cargar más'}
          </button>
        )}
      </div>
    </div>
  );
};

export default Pokedex;
