import React from 'react';
import '../styles/PopUp.css';

const PopUp = ({ pokemon, onClose }) => {
    if (!pokemon) return null;

    // Toma la habilidad principal (la primera)
    const mainAbility = pokemon.abilities?.[0]?.ability?.name;

    return (
        <div className="popup-overlay" onClick={onClose}>
            <div className="popup-content" onClick={e => e.stopPropagation()}>
                <button className="popup-close" onClick={onClose}>X</button>
                <div className="popup-flex">
                    <div className="popup-left">
                        <img src={pokemon.sprites.front_default} alt={pokemon.name} className="popup-sprite" />
                        <div className="popup-types">
                            {pokemon.types.map(t => (
                                <span key={t.type.name} className={`type-badge type-${t.type.name}`}>{t.type.name}</span>
                            ))}
                        </div>
                    </div>
                    <div className="popup-right">
                        <h2>{pokemon.name} #{pokemon.id}</h2>
                        <div>
                            <strong>Habilidad:</strong> {mainAbility}
                        </div>
                        <div>
                            <strong>Altura:</strong> {pokemon.height}
                        </div>
                        <div>
                            <strong>Peso:</strong> {pokemon.weight}
                        </div>
                        <div>
                            <strong>Stats:</strong>
                            <ul>
                                {pokemon.stats.map(stat => (
                                    <li key={stat.stat.name}>{stat.stat.name}: {stat.base_stat}</li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PopUp;
